import React, { useState } from 'react'
import './Homepage.css'


export default function Homepage() {
    const[data,setData]=useState([{
        id:0,heading:"Choose your Local Targetting",Content:"lorem dbvdkvbd dkjbdv bdvsvh sdvh dvbhdv  dbvh bdvdsbv bdvbd svbsdv  bdujbdj",status:false
    },{
        id:1,heading:"Track your Conversion",Content:"lorem dbvdkvbd dkjbdv bdv svhs dvhd vbhdv  dbvhbd vdsbv bdvbds vbsdv  bdujbdj",status:false
    },{
        id:2,heading:"Customized Campaigns",Content:"lorem dbvdkvbd dkjbdv bdvsvhs dvhdvbhdv  dbvhb dvdsbv bdvbdsvbsdv  bdujbdj",status:false
    },{
        id:3,heading:"Get dedicated support team ",Content:"lorem dbvdkvbd dkjbdvbd vsvhsdv hdv bhdv  dbvhb dvdsbv bdvbdsvbsdv  bdujbdj",status:false
    },{
        id:4,heading:"Quality Assurance",Content:"lorem dbvdkvbd dkjbdvbd vsvhsdvh dvbhdv  dbvhbd vdsbv bdvbdsv bsdv  bdujbdj",status:false
    }]) 
    function handleHover(index) {
        data[index].status=true
        setData([...data])
    }
    function handleHoverrealease(index) {
        data[index].status=false
        setData([...data])
    }
  return (
    <div className='Homepage'>
        <div className="homepage_heading">
            <h1>Why the Industry chooses Clickdee?</h1>
            <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Laboriosam eveniet minima dicta veniam ab autem, sequi omnis quis cupiditate soluta impedit tempora facere ex nisi rem debitis natus, totam odio!</p>
        <span className="hover">
          <div className="Hovercontent">
              <div className="content">
                 {data && data.map((el,index)=>(
                    <div className='elements' key={index}>
                        <h3 onMouseOver={()=>handleHover(index)}
                        onMouseLeave={()=>handleHoverrealease(index)}>{el.heading}</h3>
                        {el.status && <p>{el.Content}</p>}
                        </div>
                 ))}
              </div>
          </div>
       </span>
       </div>
    </div>
  )
}
